using System;
using System.Collections.Generic;
using System.Threading.Tasks;
using hzero.efcorecodefirst.Lib.Geo;
using hzero.efcorecodefirst.web.Models;
using Microsoft.AspNetCore.Mvc;
using hzero.efcorecodefirst.DataModel;

namespace hzero.efcorecodefirst.web.Controllers
{
	[Route("api/[controller]")]
	public class CourtFinderController : Controller
	{
		private readonly IGeoHelper _geoHelper;
		private readonly Random _randy;

		public CourtFinderController(
			IGeoHelper geoHelper)
		{
			_geoHelper = geoHelper;
			_randy = new Random(Environment.TickCount);
		}

		[HttpPost]
		[Route("FindCourts")]
		public async Task<IActionResult> FindCourts(
			[FromForm]decimal swLat,
			[FromForm]decimal swLng,
			[FromForm]decimal neLat,
			[FromForm]decimal neLng)
			=> await Task.Run(() =>
				new JsonResult(new[]
				{
					new SearchResult { Uid = Guid.NewGuid(), Location = "Test 1", Rating = 3.4m, RatingCount = 10, Lat = GetRandomDecimal(swLat, neLat), Lng = GetRandomDecimal(swLng, neLng) },
					new SearchResult { Uid = Guid.NewGuid(), Location = "Test 2", Rating = 4.4m, RatingCount = 47, Lat = GetRandomDecimal(swLat, neLat), Lng = GetRandomDecimal(swLng, neLng) },
					new SearchResult { Uid = Guid.NewGuid(), Location = "Test 3", Rating = 2.5m, RatingCount = 30, Lat = GetRandomDecimal(swLat, neLat), Lng = GetRandomDecimal(swLng, neLng) },
					new SearchResult { Uid = Guid.NewGuid(), Location = "Test 4", Rating = 3.1m, RatingCount = 12, Lat = GetRandomDecimal(swLat, neLat), Lng = GetRandomDecimal(swLng, neLng) },
					new SearchResult { Uid = Guid.NewGuid(), Location = "Test 5", Rating = 5.0m, RatingCount = 1 , Lat = GetRandomDecimal(swLat, neLat), Lng = GetRandomDecimal(swLng, neLng) }
				})
			);

		[HttpPost]
		[Route("FindLocation")]
		public async Task<IActionResult> FindLocation(
			[FromForm]string location)
			=> await Task.Run(() =>
			{
				IDictionary<string, (decimal lat, decimal lng)> result = _geoHelper.Geocode(location);
				if (!result.ContainsKey(location))
				{
					throw new Exception($"unable to locate {location}");
				}

				return new JsonResult(new
				{
					Lat = result[location].lat,
					Lng = result[location].lng
				});
			});[HttpPost]
		
		[HttpGet]
		[Route("GetCourtDetail")]
		public async Task<IActionResult> GetCourtDetail(
			[FromQuery]Guid uid)
			=> await Task.Run(() =>
			{
				return new JsonResult(new CourtDetail
				{
					CourtUid = uid,
					Format = (BasketballCourtFormats)_randy.Next(1,3),
					Location = (BasketballCourtLocations)_randy.Next(1,2)
				});
			});

		[HttpGet]
		[Route("GetCourtReviews")]
		public async Task<IActionResult> GetCourtReviews(
			[FromQuery]Guid uid)
			=> await Task.Run(() =>
			{
				return new JsonResult(new []
				{
					new CourtReview { CourtUid = uid, Rating = _randy.Next(1, 5), Reviewer = "Test 1", Review = "In pulvinar nunc nec tristique vehicula. Phasellus sed massa scelerisque, hendrerit tellus vitae, gravida arcu. Ut ornare eros diam, quis vehicula lacus tristique sit amet. Phasellus egestas nulla nec nulla lacinia ullamcorper. Maecenas pulvinar ipsum sed augue porttitor, eget condimentum sem vehicula. Curabitur fermentum tempus orci, at volutpat justo cursus vel. Fusce maximus tellus ante, eu tempus lectus commodo id. Curabitur at quam ut ex pretium lacinia at sit amet lorem. Interdum et malesuada fames ac ante ipsum primis in faucibus. Etiam quis ultrices augue. Quisque nec ante vulputate, rhoncus enim id, vestibulum turpis. Nam non felis vel magna facilisis dignissim et quis orci. Vestibulum in semper lacus." },
					new CourtReview { CourtUid = uid, Rating = _randy.Next(1, 5), Reviewer = "Test 2", Review = "Mauris faucibus rutrum lacus, eget sodales risus finibus et. Aliquam quis nibh et tellus mattis." },
				});
			});
		
		[HttpGet]
		[Route("GetCourtThumb")]
		public async Task<IActionResult> GetCourtThumb(
			[FromQuery]Guid uid,
			[FromQuery]int idx)
			=> await Task.Run(() =>
			{
				return new FileContentResult(
					Convert.FromBase64String(""),
					"image/jpeg");
			});

		
		private decimal GetRandomDecimal(decimal dec1, decimal dec2)
		{
			decimal min = dec1 < dec2 ? dec1 : dec2;
			decimal max = dec1 < dec2 ? dec2 : dec1;
			decimal delta = max - min;

			return min + delta * (decimal)_randy.NextDouble();
		}
	}
}
